@mixin section($color:inherit) {
    padding:space(8, 8) 0;
    background:$color;
}

.section {
    @include section();
}

.section__title {
    border-bottom:1px solid $light-color;
    font-size:2.8em;
    line-height:1.5em;
    margin-bottom: space(1, 8);
    padding-left:5px; // Fix left shift of the font
}

.section__details {
    text-align: center;
}

